mapper.insert:
INSERT INTO MEMBER
	(
		  MEMBER_ID
		, MEMBER_PWD
		, KOR_NAME
		, ENG_NAME
		, GROUP_CODE
		, AFFILIATION
		, RESEARCHER_ID
		, MOBILE_NO
		, RES_REGISTER_ID
		, POSITION
		, EMAIL
		, BANK
		, ACCOUNT
		, EDU_LVL
		, GUARDIAN_MOBILE
		, NAS_LINK
		, REMARK
		, REG_DT
		, REG_ID
		, MOD_DT
		, MOD_ID
	) VALUES (
		  #{memberId}
		, #{memberPwd}
		, #{korName}
		, #{engName}
		, #{groupCode}
		, #{affiliation}
		, #{researcherId}
		, #{mobileNo}
		, #{resRegisterId}
		, #{position}
		, #{email}
		, #{bank}
		, #{account}
		, #{eduLvl}
		, #{guardianMobile}
		, #{nasLink}
		, #{remark}
		, CURRENT_TIMESTAMP()
		, #{regId}
		, CURRENT_TIMESTAMP()
		, #{modId}
	)

mapper.update:
	UPDATE MEMBER
	   SET KOR_NAME = #{korName}
         , ENG_NAME = #{engName}
         , GROUP_CODE = #{groupCode}
         , AFFILIATION = #{affiliation}
         , RESEARCHER_ID = #{researcherId}
         , MOBILE_NO = #{mobileNo}
         , RES_REGISTER_ID = #{resRegisterId}
         , POSITION = #{position}
         , EMAIL = #{email}
         , BANK = #{bank}
         , ACCOUNT = #{account}
         , EDU_LVL = #{eduLvl}
         , GUARDIAN_MOBILE = #{guardianMobile}
         , NAS_LINK = #{nasLink}

	 WHERE MEMBER_ID = #{memberId}
