mapper.insert:
INSERT INTO member
	(
		  member_id
		, member_pwd
		, role
		, kor_name
		, eng_name
		, group_code
		, affiliation
		, researcher_id
		, mobile_no
		, res_register_id
		, position
		, email
		, bank
		, account
		, edu_lvl
		, guardian_mobile
		, nas_link
		, remark
		, reg_dt
		, reg_id
		, mod_dt
		, mod_id
	) VALUES (
		  #{memberId}
		, #{memberPwd}
		, #{role}
		, #{korName}
		, #{engName}
		, #{groupCode}
		, #{affiliation}
		, #{researcherId}
		, #{mobileNo}
		, #{resRegisterId}
		, #{position}
		, #{email}
		, #{bank}
		, #{account}
		, #{eduLvl}
		, #{guardianMobile}
		, #{nasLink}
		, #{remark}
		, CURRENT_TIMESTAMP()
		, #{regId}
		, CURRENT_TIMESTAMP()
		, #{modId}
	)

mapper.update:
	UPDATE member
	   SET kor_name 		= #{korName}
         , eng_name 		= #{engName}
         , role				= #{role}
         , group_code 		= #{groupCode}
         , affiliation 		= #{affiliation}
         , researcher_id 	= #{researcherId}
         , mobile_no 		= #{mobileNo}
         , res_register_id 	= #{resRegisterId}
         , position 		= #{position}
         , email 			= #{email}
         , bank 			= #{bank}
         , account 			= #{account}
         , edu_lvl 			= #{eduLvl}
         , guardian_mobile 	= #{guardianMobile}
         , nas_link 		= #{nasLink}
         , remark			= #{remark}
         , mod_dt			= CURRENT_TIMESTAMP()
         , mod_id			= #{modId}

         , member_pwd 		= #{changedPwd}

	 WHERE member_id = #{memberId}

mapper.select:
SELECT COUNT(*)
  FROM member
 WHERE member_id = #{memberId}

SELECT COUNT(*)
  FROM member
 WHERE member_id = #{memberId}
   AND member_pwd = #{memberPwd}

SELECT member_pwd
  FROM member
 WHERE member_id = #{memberId}
