mapper.insert:
INSERT INTO member
	(
		  member_id
		, member_pwd
		, role
		, kor_name
		, eng_name
		, mobile_no
		, email
		, reg_dt
		, reg_id
		, mod_dt
		, mod_id
	) VALUES (
		  #{memberId}
		, #{memberPwd}
		, #{role}
		, #{korName}
		, #{engName}
		, #{mobileNo}
		, #{email}
		, CURRENT_TIMESTAMP()
		, #{regId}
		, CURRENT_TIMESTAMP()
		, #{modId}
	)

mapper.update:
	UPDATE member
	   SET kor_name 		= #{korName}
         , eng_name 		= #{engName}
         , role				= #{role}
         , mobile_no 		= #{mobileNo}
         , email 			= #{email}
         , mod_dt			= CURRENT_TIMESTAMP()
         , mod_id			= #{modId}

         , member_pwd 		= #{changedPwd}

	 WHERE member_id = #{memberId}

mapper.select:
SELECT COUNT(*)
  FROM member
 WHERE member_id = #{memberId}

SELECT COUNT(*)
  FROM member
 WHERE member_id = #{memberId}
   AND member_pwd = #{memberPwd}

SELECT member_pwd
  FROM member
 WHERE member_id = #{memberId}
